mapper.select:
select a.id, a.name, u.name owner, a.startDate, a.endDate from tbl_activity a join tbl_user u on a.owner=u.id
a.name like '%' #{name} '%' and u.name like '%' #{owner} '%' and a.startDate > #{startDate} and a.endDate < #{endDate}
order by a.createTime desc limit #{skipCount},#{pageSize}

mapper.delete:
delete from tbl_activity
where id in


	#{aid}

mapper.update:
update tbl_activity set
owner=#{owner}, name=#{name}, startDate=#{startDate}, endDate=#{endDate}, cost=#{cost}, description=#{description}, editTime=#{editTime}, editBy=#{editBy}
where id=#{id}
